//: Playground - noun: a place where people can play
import Foundation

var str = "Hello, playground"

func calculateHash(input:String) -> Int {
    let integers = input.map { Int(String($0)) }
    var accu = 0
    var previous:Int = integers.last!!
    for integer in integers {
        if(previous == integer) {
            accu += integer!
        }
        previous = integer!
    }
    return accu;
}

calculateHash(input: "1122") == 3
calculateHash(input: "1111") == 4
calculateHash(input: "1234") == 0
calculateHash(input: "91212129") == 9
calculateHash(input: "")

func calculateCircularHash(input:String) -> Int {
    let integers = input.map { Int(String($0)) }
    let midway = integers.count / 2
    var accu = 0
    var compareToIndex = 0
    for (index, integer) in integers.enumerated() {
        if(index < midway) {
            compareToIndex = index + midway
        } else {
            compareToIndex = index - midway
        }
        if(integers[compareToIndex] == integer) {
            accu += integers[compareToIndex]!
        }
    }
    return accu;
}

calculateCircularHash(input: "1212") == 6
calculateCircularHash(input: "1221") == 0
calculateCircularHash(input: "123425") == 4
calculateCircularHash(input: "123123") == 12
calculateCircularHash(input: "12131415") == 4
calculateCircularHash(input: "")

